import React from 'react';
import './App.css';
import './bootstrap.min.css';
import Listitems from './Listitems.js';
import Cart from './Cart.js';
import Products from './Products.json'


class App extends React.Component{
	constructor(props){
		super(props);
		this.handler = this.handler.bind(this)
		this.state = {
			productLists:[],
			inCart:[],
			viewCart:['test']
		}
	}

	handler(object) {
	    this.setState({
	      inCart: object,
	      viewCart:this.changeViewCart(this.props.inCart)
	    });   
	}
	changeViewCart(object){
		return(
				<div class="py-3 px-2 pb-0 shadow border mb-3 cartNotEmpty">
						<div>
							<table class="table thead-dark table-responsive">
								<thead class="thead-dark">
									<tr>
										<th class="w-25 d-none d-md-table-cell">Image</th>
										<th>Produit</th>	
										<th>Reference</th>
										<th>Quantités</th>
										<th>Prix</th>
										<th></th>
									</tr>
								</thead>
								<tbody class="align-middle">
										{this.state.inCart.map(function(object, i){
											return( 
												<tr key={i}>
													<td class="w-25 d-none d-md-table-cell">
														<img src={object.image} alt="" class="w-100"/>
													</td>
													<td class="w-50 align-middle">
													{object.title.name}
							            				<br/>
									            		<small class="text-secondary">{object.title.details}</small>
								            		</td>
										            <td class="align-middle">
										            	<small>Ref: {object.ref} </small>
										            </td>
										            <td class="align-middle text-right">
										            	<div role="group" class="btn-group">
										            		<button type="button" class="btn btn-light">-</button>
										            		<button type="button" class="btn btn-disable">{object.quantity}</button>
										            		<button type="button" class="btn btn-light">+</button>
										            	</div>
									            	</td>
									            	<td class="align-middle">{object.price}&nbsp;€</td>
									            	<td class="align-middle">
										            	<button title="supprimer" type="button" class="btn btn-outline-dark">X</button>
									            	</td>
												</tr>
											)
										})}
		            			</tbody>
	            			</table>
            			</div>
        			</div>
			)

	}

	render(){
		return (
			<div class="App">
				<div class="container-fluid">
					<nav class="navbar navbar-light bg-light shadow w-100">
					  	<a class="navbar-brand" href="#">Navbar</a>
					  	<img alt="logo" src="logo.png"/>
					</nav>
					<div id="mdm-cart" class="container">
						<h1 class="title border-bottom mb-5 display-1">Mon panier</h1>
						<div class="content">
							<div class="basket col-8">
								<Cart  viewCart={this.state.viewCart} inCart={this.state.inCart}/>
							</div>
							<div class="liste col-4">
								<h2>Produits en relation :</h2>
								<Listitems key={Products.id} data={Products} handler={this.handler} inCart={this.state.inCart}/>
							</div>
						</div>
					</div>
				</div>
			</div>
	  );
	}	
}

export default App;
