import React from 'react';
import './App.css';
import './bootstrap.min.css';


class Cart extends React.Component{
	constructor(props){
		super(props);
		this.changeViewCart = this.changeViewCart.bind(this)
		this.state = {
			viewCart : [this.props.viewCart],
			inCart:[]
		}
	}
	changeViewCart(obj){
		if(obj.length !== 0){
			return(
				<div class="py-3 px-2 pb-0 shadow border mb-3 cartNotEmpty">
						<div>
							<table class="table thead-dark table-responsive">
								<thead class="thead-dark">
									<tr>
										<th class="w-25 d-none d-md-table-cell">Image</th>
										<th>Produit</th>	
										<th>Reference</th>
										<th>Quantités</th>
										<th>Prix</th>
										<th></th>
									</tr>
								</thead>
								<tbody class="align-middle">
										{obj.map(function(object, i){
											return( 
												<tr key={i}>
													<td class="w-25 d-none d-md-table-cell">
														<img src={object.image} alt="" class="w-100"/>
													</td>
													<td class="w-50 align-middle">
													{object.title.name}
							            				<br/>
									            		<small class="text-secondary">{object.title.details}</small>
								            		</td>
										            <td class="align-middle">
										            	<small>Ref: {object.ref} </small>
										            </td>
										            <td class="align-middle text-right">
										            	<div role="group" class="btn-group">
										            		<button type="button" class="btn btn-light">-</button>
										            		<button type="button" class="btn btn-disable">{object.quantity}</button>
										            		<button type="button" class="btn btn-light">+</button>
										            	</div>
									            	</td>
									            	<td class="align-middle">{object.price}&nbsp;€</td>
									            	<td class="align-middle">
										            	<button title="supprimer" type="button" class="btn btn-outline-dark">X</button>
									            	</td>
												</tr>
											)
										})}
		            			</tbody>
	            			</table>
            			</div>
        			</div>
			)
		}else{
			return(
				<div class="cartEmpty">
					<h2 class="display-5 text-center mt-1">Votre panier est vide</h2>
				</div>
			)
		}
	}
	addToCart (object){
		if(this.state.ref.length === 0){
			this.state.ref.push(object.ref);
			this.state.inCart.push(object);
		}else{
			var x;
			if(this.state.ref.indexOf(object.ref) > -1){
				for(x in this.state.inCart){
					if(this.state.inCart[x].ref === object.ref){
						this.state.inCart[x].quantity++;
					}
				}
			}else{
				this.state.ref.push(object.ref);
				this.state.inCart.push(object);
			}
		}
		this.props.handler(this.state.inCart);
	}
	render(){
		return (
			<div>
				<div class="py-3 px-2 pb-0 shadow border mb-3">
					{this.props.viewCart}
				</div>
				<div class="d-flex bg-dark p-2 text-light mb-3">
					<div>
						<strong>Total: </strong>
					</div>
					<div class="ml-auto">
						<strong>0.00 €</strong>
					</div>
				</div>
				<div class="d-flex mb-5">
					<button type="button" class="btn ml-auto mr-3 text-dark btn-outline-warning">Tout supprimer</button>
            		<button type="button" class="btn btn-primary">Valider le panier</button>
            	</div>
			</div>
	  	);
	}	
}

export default Cart;
