import React from 'react';
import './App.css';
import './bootstrap.min.css';


class Listitems extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			view : [],
			Listitems:[this.props],
			inCart:[]
		}
	}
	addToCart (object){
		console.log(this);
		this.setState({view:'test'})

	}
	test(){
		console.log(this.state.inCart)
	}
	render(){
		var thisFunction = this
		return (
			<div>
				<div class="row mb-5">
					<ul>
		                {this.state.Listitems[0].data.map(function(object, i ){
		                	console.log(test)
		                	return( 
			                	<li key={i} class="item">
			                		<div class="col">
										<img src={object.image} onClick={() => {thisFunction.test()}} alt="Canapé-lit panoramique 7 places gris clair" class="w-100"/>
									</div>
									<div class="col">
										<strong id="description">{object.title.details}</strong>
										<div id="price" class="text-secondary font-weight-bold mb-2">{object.price}€</div>
				          				<button type="button" class="btn btn-secondary" onClick={() => {thisFunction.setState({cart:object.ref})}}>
				          					<small>Ajouter au panier</small>
				          				</button>
				          			</div>
						        </li>
						    )    
					    })}
					</ul>
          		</div>
			</div>
	  	);
	}	
}

export default Listitems;
