import React from 'react';
import './App.css';
import './bootstrap.min.css';
import Listitems from './Listitems.js';
import Cart from './Cart.js';
import Products from './Products.json'


class App extends React.Component{
	constructor(props){
		super(props);
		this.handler = this.handler.bind(this)
		this.state = {
			productLists:[],
			inCart:["t"]
		}
	}

	handler(object) {

	    this.setState({
	      inCart: object
	    })
	}

	render(){
		return (
			<div class="App">
				<div class="container-fluid">
					<nav class="navbar navbar-light bg-light shadow w-100">
					  	<a class="navbar-brand" href="#">Navbar</a>
					  	<img onClick={() => {console.log(this.state)}} src="logo.png"/>
					</nav>
					<div id="mdm-cart" class="container">
						<h1 class="title border-bottom mb-5 display-1">Mon panier</h1>
						<div class="content">
							<div class="basket col-8">
								<Cart  productInCart={this.state.productLists} />
							</div>
							<div class="liste col-4">
								<h2>Produits en relation :</h2>
								<Listitems key={Products.id} data={Products} handler={this.handler} inCart={this.inCart}/>
							</div>
						</div>
					</div>
				</div>
			</div>
	  );
	}	
}

export default App;
