import React from 'react';
import './App.css';
import './bootstrap.min.css';


class Listitems extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			inCart : [],
			Listitems:[this.props]
		}
	}
	addToCart (object){
		for(var i = 0; i <= this.state.inCart.length;i++){
			console.log(object)
		}
		if(this.state.inCart.indexOf(object.ref) === -1){
    		this.state.inCart.push(object);
    	}else{
    		object.quantity !== NaN ? object.quantity = object.quantity + 1 : object.quantity = 1;
    		this.state.inCart.push(object);
    	}
    	this.props.handler(this.state.inCart)
	}
	render(){
		var thisFunction = this
		return (
			<div>
				<div class="row mb-5">
					<ul>
		                {this.props.data.map(function(object, i ){
		                	return( 
			                	<li key={i} class="item">
			                		<div class="col">
										<img src={object.image} alt="Canapé-lit panoramique 7 places gris clair" class="w-100"/>
									</div>
									<div class="col">
										<strong id="description">{object.title.details}</strong>
										<div id="price" class="text-secondary font-weight-bold mb-2">{object.price}€</div>
				          				<button type="button" class="btn btn-secondary" onClick={() => {thisFunction.addToCart(object)}}>
				          					<small>Ajouter au panier</small>
				          				</button>
				          			</div>
						        </li>
						    )    
					    })}
					</ul>
          		</div>
			</div>
	  	);
	}	
}

export default Listitems;
